// REDUCERS
import { overPageShow } from '../../redux/reducers/overPageSlice';
// =====================================================
// LOCATION
import Parameter from '../Parameter/Prameter';
// =====================================================
/* HOOKS */
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
// =====================================================
// FONT AWESOME
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faAngleDown, faSearch} from '@fortawesome/free-solid-svg-icons';
// =====================================================

const Links = () =>{
    
    const { navbarShow } = useSelector( state => state.navbar ); // SELECT NAVBAR STATE FROM STORE
    
    const dispatch = useDispatch(); // DISPATCH
    // TOGGLE OVERPAGE HANDLE
    const HandleSearchShow = () => {
        dispatch( overPageShow({content:'search'}) );
    }

    let parameterFirst = Parameter().parameterFirst;

    return(
        <div className={
            // TOGGLE MENU BY MOBILE BUTTON MENU
            navbarShow === 0 ? 'links flex-center links-mobile' 
                             : 'links flex-center links-mobile active'
        }>
            <ul className="flex-center">
                <li><a className={ parameterFirst === 'porto/' ? 'active' : '' } href='/porto/'>Home</a></li>
                <li><a className={ parameterFirst === 'porto/about' ? 'active' : '' } href='/porto/about'>About</a></li>
                <li className='services-menu-link'>
                    <a className={ parameterFirst === 'porto/services' ? 'active' : '' } href='/porto/services'>Services <FontAwesomeIcon icon={faAngleDown} /></a>
                    <ul className='services-menu'>
                        <li><a href='/porto/services'>View All</a></li>
                        <li><a href='/porto/services/1'>Design & Planning</a></li>
                        <li><a href='/porto/services/2'>Interior Design</a></li>
                        <li><a href='/porto/services/3'>Exterior Design</a></li>
                        <li><a href='/porto/services/4'>Construction Drawings</a></li>
                    </ul>
                </li>
                <li><a className={ parameterFirst === '/porto/blogs' ? 'active' : '' } href='/porto/blogs'>Blogs</a></li>
                <li><a className={ parameterFirst === '/porto/contact' ? 'active' : '' } href='/porto/contact'>Contact</a></li>
                {/* SEARCH BUTTON */}
                <li onClick={HandleSearchShow}><a href='#/'><FontAwesomeIcon icon={faSearch} /></a></li>
            </ul>
        </div>
    );
}
export default Links;
