// STYLE
import './AllBlogs.css';
// ==========================================================
// IMAGES
import Image_1 from '../../Home/main_imgs/blog-thumb-1.jpg';
import Image_2 from '../../Home/main_imgs/blog-thumb-2.jpg';
import Image_3 from '../../Home/main_imgs/blog-thumb-3.jpg';
// ==========================================================
// FONT AWESOME
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRightToBracket } from '@fortawesome/free-solid-svg-icons';
// ==========================================================
// LOCAL API
import Api from '../../../../Api/Api';
// ==========================================================

const AllBlogs = () => {
    let images = [ Image_1, Image_2, Image_3, Image_1 ];
    let blogs = Api().AllBlogs;
    return(
        <section className="allBlogs">
            <div className="page-header">
                <div className='container'>
                    <div className="page-header-content">
                        <h1>Blogs</h1>
                        <h4><a href="/">Home</a> / Blogs</h4>
                    </div>
                </div>
            </div>
            <div className='container'>
                <div className="blogs-content grid-6-pages-cols">
                    <div className="blogs littleFullWidth grid-1-5-full">
                        { blogs.map(( el, indx ) => {
                            return(
                                <div className='blog-single' key={indx}>
                                    <img src={images[indx]}  alt='blog-img' />
                                    <div className='blog-data'>
                                        <span>{el.blogData} | BY {el.blogCreate}</span>
                                    </div>
                                    <div className='blog-title'>
                                        <h1><a href="#/">{el.blogTitle}</a></h1>
                                    </div>
                                    <div className='blog-text'>
                                        <p>{el.blogBody}</p>
                                    </div>
                                    <div className='blog-readMore'>
                                        <button><FontAwesomeIcon icon={faArrowRightToBracket} /></button>
                                    </div>
                                </div>
                            )
                        }) }
                    </div>
                    <div className='blog-side littleFullWidth grid-5-6-full'>
                        <div className='blog-side-content'>
                            <h3>About The Blog</h3>
                            <p>Lorem ipsum dolor sit amet, conse elit porta. Vestibulum ante justo, volutpat quis porta diam.</p>
                        </div>
                        <div className='blog-side-content'>
                            <form>
                                <input type='text' placeholder='Search' />
                            </form>
                        </div>
                        <div className='blog-side-content'>
                            <h3>Recent Posts</h3>
                            <div className='blog-single'>
                                { blogs.map(( el, indx ) => {
                                    return(
                                        <>
                                            <div className='blog-data'>
                                                <span>{el.blogData} | BY {el.blogCreate}</span>
                                            </div>
                                            <div className='blog-title'>
                                                <h1><a href="#/">{el.blogTitle}</a></h1>
                                            </div>
                                        </>
                                    )
                            }) }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}
export default AllBlogs;
