import './slider.css';
import './slider.responsive.css';
/* == FONT AWESOME == */
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faCaretLeft,faCaretRight} from '@fortawesome/free-solid-svg-icons';
/* == ------------ == */
/* == DATA FROM JSON == */
import Data from '../../../../Api/ApiData.json';
/* == HOOKS == */
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
/* == REDUCERS == */
import { SliderReducer, SliderShow } from '../../../redux/reducers/mainSliderSlice';
/* == ------------ == */
const MainSlider = () => {
    const dispatch = useDispatch();
    const { direction, show, numSlider } = useSelector( state => state.mainSlider );
    const handleSlider = (direct, move) =>{
        dispatch( SliderReducer() );
        let countData = Data.SliderContent_.length;
        setTimeout(()=>{
            dispatch( SliderShow({ actionDirection:direct,move:move }) );
        },500)
    }
    return(
        <section id='main-slider-section' className="main-slider-section">
            <div className='slider-content content-container'>
                <div className='slider-btns'>
                    <div className='slider-prev' onClick={()=>handleSlider('left','prev')}><FontAwesomeIcon icon={faCaretLeft} /></div>
                    <div className='slider-next' onClick={()=>handleSlider('right','next')}><FontAwesomeIcon icon={faCaretRight} /></div>
                </div>
                <div className={`slider-imgs ${direction}`}>
                    <div className='slider-img-1'></div>
                    <div className='slider-img-2'></div>
                </div>
                <div className='slider-text'>
                    <div className={ `slider-text-content ${direction + ' ' + show}` }>
                        <h3>{ Data.SliderContent_[numSlider].sliderTitle }</h3>
                        <p>{Data.SliderContent_[numSlider].sliderDesc}</p>
                        <p>{Data.SliderContent_[numSlider].sliderText}</p>
                        <span className='slider-btn-border'></span>
                        <button className='btn btn-light'>{Data.SliderContent_[numSlider].sliderBtn}</button>
                    </div>
                </div>
            </div>
        </section>
    )
}
export default MainSlider;
