import { createSlice } from "@reduxjs/toolkit";

const overPageSlice = createSlice({
    name:'overPage-show',
    initialState:{
        overPageShow:0
    },
    reducers:{
        overPageReducer:(state) => {
            let overPageState = state.overPageShow;
            if( overPageState === 0 ){
                state.overPageShow = 1;
            }else{
                state.overPageShow = 0;
            }
        }
    }
});

export const { overPageReducer } = overPageSlice.actions;
export default overPageSlice.reducer;
