// FONT AWESOME
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faSwatchbook,faPaintRoller,faDrawPolygon} from '@fortawesome/free-solid-svg-icons';
// =======================================================
// SERVICES STYLES
import './Services.css';
import './Services_responsive.css';
// =======================================================
// FETCH SERVICES DATA
import Api from '../../../../Api/Api';
import { useState } from 'react';
// =======================================================

const Services = () => {
    const [ serviceIcon_, setServiceIcon ] = useState('');
    let servicesDate = Api().ServicesContent;
    return(
        <section className="container services-content">
            <div className={`grid-3-cols services`}>
                {servicesDate.map((el,indx)=>{
                    switch(el.serviceIcon){
                        case 'faSwatchbook':
                            return setServiceIcon(faSwatchbook);
                        case 'faPaintRoller':
                            return setServiceIcon(faPaintRoller);
                        case 'faDrawPolygon':
                            return setServiceIcon(faDrawPolygon);
                            
                    }
                    return(
                        <div className={`single-service`} key={indx}>
                            <div className='service-icon'><FontAwesomeIcon icon={serviceIcon_} /></div>
                            <div className='service-header'><h2>{el.serviceHeader}</h2></div>
                            <div className='service-content'>
                                <p>{el.serviceContent}</p>
                            </div>
                        </div>
                    )
                })}
            </div>
            <div className='circles-slider flex-center'>
                <span 
                    className='circle-slider active' >
                </span>
                <span 
                    className='circle-slider' >
                </span>
                <span 
                    className='circle-slider' >
                </span>
                <span 
                    className='circle-slider' >
                </span>
            </div>
        </section>
    )
}

export default Services;
