import { useRef, useState } from 'react';
import './Counter.css';
const Counter = () => {

    const [elementVal_1, setelementVal_1] = useState(0);
    const [elementVal_2, setelementVal_2] = useState(0);
    const [elementVal_3, setelementVal_3] = useState(0);
    const [elementVal_4, setelementVal_4] = useState(0);

    let elementRef_1 = useRef(),
        elementRef_2 = useRef(),
        elementRef_3 = useRef(),
        elementRef_4 = useRef(),
        elementRef = useRef();

    window.addEventListener('scroll', () => {
        
    })

    return(
        <div ref={elementRef} className="grid-4-cols counter">
            <div className="grid-counter"><h1 ref={elementRef_1} data-goal={15}>{elementVal_1}+</h1><p data-aos='fade-up' data-aos-delay='300'>YEARS OF EXPERIENCE</p></div>
            <div className="grid-counter"><h1 ref={elementRef_2} data-goal={350}>{elementVal_2}+</h1><p data-aos='fade-up' data-aos-delay='500'>SATISFIED CLIENTS</p></div>
            <div className="grid-counter"><h1 ref={elementRef_3} data-goal={20}>{elementVal_3}+</h1><p data-aos='fade-up' data-aos-delay='700'>TEAM MEMBERS</p></div>
            <div className="grid-counter"><h1 ref={elementRef_4} data-goal={45}>{elementVal_4}+</h1><p data-aos='fade-up' data-aos-delay='900'>AWARDS WINNING</p></div>
        </div>
    )
}
export default Counter;
