import './slider.css';
import './slider.responsive.css';
/* == FONT AWESOME == */
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faCaretLeft,faCaretRight} from '@fortawesome/free-solid-svg-icons';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { SliderReducer } from '../../../redux/reducers/mainSliderSlice';
import { useState } from 'react';
/* == ------------ == */
const MainSlider = () => {
    const dispatch = useDispatch();
    const { direction, show } = useSelector( state => state.mainSlider );
    const [showElement,setShowElement] = useState('');
    const handleLeftSlider = () =>{
        setTimeout(()=>{
            setShowElement('showElement');
        },3000)
        dispatch( SliderReducer({ showElement:showElement }) );
    }
    return(
        <section id='main-slider-section' className="main-slider-section">
            <div className='slider-content content-container'>
                <div className='slider-btns'>
                    <div className='slider-prev'><FontAwesomeIcon icon={faCaretLeft} /></div>
                    <div className='slider-next' onClick={handleLeftSlider}><FontAwesomeIcon icon={faCaretRight} /></div>
                </div>
                <div className={`slider-imgs ${direction}`}>
                    <div className='slider-img-1'></div>
                    <div className='slider-img-2'></div>
                </div>
                <div className='slider-text'>
                    <div className={ `slider-text-content ${direction + ' ' + show}` }>
                        <h3>1</h3>
                        <p>2</p>
                        <p>3</p>
                        <span className='slider-btn-border'></span>
                        <button className='btn btn-light'>4</button>
                    </div>
                </div>
            </div>
        </section>
    )
}
export default MainSlider;
