import { createSlice } from "@reduxjs/toolkit";

const FeedbackSlice = createSlice({
    name:'Feedback',
    initialState:{
        feedbackPosition:'left_1',
        indx: 1
    },
    reducers:{
        FeedbackMoving:( state, action ) => {
            if( action.payload.i === 4 ){
                state.feedbackPosition = `left_1`;
            }
            state.feedbackPosition = `left_${action.payload.i}`;
            state.indx = action.payload.i;
        }
    }
});

export const { FeedbackMoving } = FeedbackSlice.actions;
export default FeedbackSlice.reducer;
