/* FONT AWESOME */
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faWindowClose} from '@fortawesome/free-solid-svg-icons';
/* REDUCERS FROM STORE */
import {searchClose,searchSubmit} from '../../redux/reducers/overPageSlice';
/* HOOKS */
import { useDispatch } from 'react-redux';
import { useState } from 'react';
/* ===================== */
const Search = () => {
    
    const dispatch = useDispatch();
    const [ searchValue, setSearchValue ] = useState('');

    const HandleSearchValue = e => {
      let value = e.target.value;
      setSearchValue(value);
    }

    const searchSubmitValue = () => {
      dispatch( searchSubmit({value:searchValue}) )
    }

    const HandleSearchClose = e => {
      e.preventDefault();
      dispatch( searchClose() )
    }

    return(
      <div className='search'>
        <div className='container'>
            <span className='close-icon' onClick={HandleSearchClose}>
                <FontAwesomeIcon icon={faWindowClose}></FontAwesomeIcon>
            </span>
            <form onSubmit={searchSubmitValue}>
              <input
                  id='search_id'
                  type='text'
                  className='input'
                  placeholder='Type Text here ...'
                  onChange={HandleSearchValue}
              />
            </form>
        </div>
      </div>
    )
  };
  export default Search;
