// FONT AWESOME
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faAngleRight} from '@fortawesome/free-solid-svg-icons';
// ============================================
// STYLES
import './Comments.css';
import './Comments_responsive.css';
// ============================================
const Comments = () => {
    
    return(
        <section className="section-comments section">
            <div className="grid-3-cols comments">
                <div className="comments-img img-1"></div>
                    <div className="comment-single">
                        <div className={`comment-content-data`}>
                        {
                            comments_datas.map( (el,indx) => {
                                return(
                                    <div className="comment-single-content" key={indx}>
                                        <div className='comment-header'>
                                            <span>{el.commentNum}</span>
                                            <h3>{el.commentTitle}</h3>
                                        </div>
                                        <div className='comment-content'>
                                            <p>{el.commentText}</p>
                                        </div>
                                    </div>
                                )
                            })
                        }
                        </div>
                        <div className='comment-btn'><FontAwesomeIcon icon={faAngleRight} /></div>
                    </div>
                <div className="comments-img img-2"></div>
            </div>
        </section>
    )
}
export default Comments;
