import { createSlice } from "@reduxjs/toolkit";

const searchSlice = createSlice({
    name:'search-show',
    initialState:{
        searchShow:0
    },
    reducers:{
        searchReducer:(state) => {
            let searchState = state.searchShow;
            if( searchState === 0 ){
                state.searchShow = 1;
            }else{
                state.searchShow = 0;
            }
        }
    }
});

export const { searchReducer } = searchSlice.actions;
export default searchSlice.reducer;
