import { createSlice } from "@reduxjs/toolkit";

const mainSliderSlice = createSlice({
    name:'main-slider',
    initialState:{
        direction:'left',
        show:'showElement'
    },
    reducers:{
        SliderReducer:(state, action) => {
            state.show = 'hideElement';
            if( state.direction === 'left' ){
                state.direction = 'right';
            }else{
                state.direction = 'left';
            }
            state.show = action.payload.showElement;
        }
    }
});

export const { SliderReducer } = mainSliderSlice.actions;
export default mainSliderSlice.reducer;
