import { createSlice } from "@reduxjs/toolkit";

const mainSliderSlice = createSlice({
    name:'main-slider',
    initialState:{
        direction:'left',
        show:'showElement',
        numSlider:0
    },
    reducers:{
        SliderReducer:(state) => {
            state.show = 'hideElement';
        },
        SliderShow:(state,action) =>{
            let direct = action.payload.actionDirection;
            let num = action.payload.numAction;
            state.numSlider = num;
            state.direction = direct;
            state.show = 'showElement';
        }
    }
});

export const { SliderReducer, SliderShow } = mainSliderSlice.actions;
export default mainSliderSlice.reducer;
