// HOOKS
import { useState } from "react";
import { useSelector } from "react-redux";
// ====================================================
// EXTENSIONS
import { BrowserRouter as Router, Switch , Route } from "react-router-dom";
// ====================================================
// PROJECT COMPONENTS
import Footer from "./content/components/Footer/Footer";
import Header from "./content/components/Header/Header";
import Home from "./content/components/Home/HomeSections/Home";
import OverPage from "./content/components/OverPages/OverPage";
import AllBlogs from "./content/components/Pages/Blogs/AllBlogs";
import Contact from "./content/components/Pages/Contact/Contact";
import NotFound from "./content/components/Pages/NotFound/NotFound";
import SinglePage from "./content/components/Pages/SinglePage/SinglePage";
import Parameter from "./content/components/Parameter/Prameter";
import UpButton from "./content/components/UpButton/UpButton";
// ====================================================
// APP STYLES
import './GlobalStyles/GlobalAnimation.css';
import './GlobalStyles/GlobalStyleTheme.css';
import './GlobalStyles/ThemeResponsive.css';
// ====================================================

function App() {

  const [ windowScroll, setWindowScroll ] = useState();
  const {overPageShow} = useSelector( state => state.overPage ); // SELECT OVERPAGE STATE FROM STORE

  let urls = [ '/', '/contact', '/blogs', '/blogs/:id' ];

  let allUrls = [ '', '/', '/contact', '/blogs',
                  '/', 'contact', 'blogs', 
                  'blogs/1', 'blogs/2', 'blogs/3', 'blogs/4'
                ];

  window.addEventListener( 'scroll', () => {
      setWindowScroll(window.pageYOffset);
  })

  let parameters = Parameter();
  let parameterFirst_ = parameters.parameterFirst.slice(0,parameters.parameterFirst.lastIndexOf('/'));
  let parameterFirst__ = parameters.parameterFirst;
  let parameterLast_ = parameters.parameterLast;
  console.log(parameterFirst_)
  console.log(parameterFirst__)
  console.log(parameterLast_)
  console.log(typeof parameterFirst_)
  console.log(typeof parameterFirst__)
  console.log(typeof parameterLast_)
  console.log(!allUrls.includes(parameterLast_))
  console.log(!allUrls.includes(parameterFirst__))
  console.log(!allUrls.includes(parameterFirst_))


  return (
    <Router>
      <div>
        { overPageShow === 1 && <OverPage /> }
        <Header />
        <Switch>
        { !(allUrls.includes(parameterFirst_)) // false 
          || !(allUrls.includes(parameterLast_)) // false
          || !(allUrls.includes(parameterFirst__)) // false
          ? 
            <Route>
              <NotFound />
            </Route>
          :
          <>
            <Route exact path={urls[0]}>
              <Home />
            </Route>
            <Route path={urls[1]}>
              <Contact />
            </Route>
            <Route exact path={urls[2]}>
              <AllBlogs />
            </Route>
            <Route path={urls[3]}>
              <SinglePage />
            </Route>
          </>
          }
        </Switch>
        <UpButton />
        <Footer />
      </div>
    </Router>
  );
}

export default App;
