import { createSlice } from "@reduxjs/toolkit";

const overPageSlice = createSlice({
    name:'overPage-show',
    initialState:{
        overPageShow:0,
        overPageContent:'',
        searchValue:''
    },
    reducers:{
        searchShow:(state) =>{
            state.overPageShow = 1;
            state.overPageContent = 'search';
        },
        searchSubmit:(state,action) => {
            state.searchValue = action.payload.value;
            console.log(state.searchValue)
        },
        searchClose:(state) =>{
            state.overPageShow = 0;
            state.overPageContent = '';
        }
    }
});

export const { searchShow, searchSubmit, searchClose } = overPageSlice.actions;
export default overPageSlice.reducer;
