import Parameter from "../../Parameter/Prameter";
import Api from "../../../../Api/Api";
import { useState } from "react";
const SinglePage = () => {

    const [ getParameter , setGetParameter ] = useState();

    let parametersValue = Parameter(),
        parameterFirst = parametersValue.parameterFirst.slice(0,parametersValue.parameterFirst.lastIndexOf('/')),
        parameterId = parametersValue.parameterLast,
        blogs = Api().AllBlogs,
        services = Api().ServicesContent;

    if( parameterFirst === 'blogs' ){
        setGetParameter(blogs)
    }else if( parameterFirst === 'services' ){
        setGetParameter(services)
    }

    return(
        <section className="allBlogs">
            <div className="page-header">
                <div className='container'>
                    <div className="page-header-content">
                        <h1>{ getParameter.blogTitle }</h1>
                        <h4><a href="/">Home</a> / Blogs / { parameterId }</h4>
                    </div>
                </div>
            </div>
            <div className='container'>
                <div className="blogs-content grid-6-pages-cols">
                    <div className="blogs littleFullWidth grid-1-5-full">
                        { blogs.map(( el, indx ) => {
                            return(
                                <div className='blog-single' key={indx}>
                                    <img src={images[indx]}  alt='blog-img' />
                                    <div className='blog-data'>
                                        <span>{el.blogData} | BY {el.blogCreate}</span>
                                    </div>
                                    <div className='blog-title'>
                                        <h1><a href={`/blogs/${indx + 1}`}>{el.blogTitle}</a></h1>
                                    </div>
                                    <div className='blog-text'>
                                        <p>{el.blogBody}</p>
                                    </div>
                                    <div className='blog-readMore'>
                                        <button><FontAwesomeIcon icon={faArrowRightToBracket} /></button>
                                    </div>
                                </div>
                            )
                        }) }
                    </div>
                    <div className='blog-side littleFullWidth grid-5-6-full'>
                        <SideBlog />
                    </div>
                </div>
            </div>
        </section>
    )
}
export default SinglePage;
