import './Skills.css';
import Image from '../main_imgs/divider.jpg';
import Api from '../../../../Api/Api';
import { useRef, useState } from 'react';

const Skills = () => {

    let countAosSkill = 0;

    let elementRef_1 = useRef(),
        elementRef_2 = useRef(),
        elementRef_3 = useRef();



    let elementRefs = [ elementRef_1, elementRef_2, elementRef_3 ];

    const [testValue_1, setTestValue_1] = useState(0);
    const [testValue_2, setTestValue_2] = useState(0);
    const [testValue_3, setTestValue_3] = useState(0);

    let elementValue_1 = 0;
    let elementValue_2 = 0;
    let elementValue_3 = 0;

    let skillValueElements = [testValue_1,testValue_2,testValue_3]

    const refFunc = () => {
        if(elementRef_1.current !== undefined && elementRef_2.current !== undefined && elementRef_3.current !== undefined){
             elementValue_1 = elementValue_1 + elementRef_1.current.getAttribute('data-goal')
             elementValue_2 = elementValue_2 + elementRef_2.current.getAttribute('data-goal')
             elementValue_3 = elementValue_3 + elementRef_3.current.getAttribute('data-goal')
        }
    }

    refFunc();

    setTimeout(
        () => {
            if( testValue_1 !== elementValue_1 && testValue_2 !== elementValue_2 && testValue_3 !== elementValue_3 ){
                setTestValue_1(testValue_1 + 1)
                setTestValue_2(testValue_2 + 1)
                setTestValue_3(testValue_3 + 1)
            }
        }
    , 10)

    const SkillsData = Api().SkillsData;
        
    return(
        <section className="section">
            <div className="grid-2-cols">
                <div className="section-header">
                    <div className='section-header-desc'><h2 className='bg-header'>IDEAS</h2></div>
                    <span data-aos='fade-down' data-aos-delay='200'>CUSTOM SERVICES</span>
                    <h1 data-aos='fade-up' data-aos-delay='200'>Design. Build. Better.</h1>
                    <img className='header-line' alt='header-line' src={Image} />
                    <p data-aos='fade-left' data-aos-delay='300' className='header-description'>Discover how our unique process improves your building experience.</p>                        
                </div>
                <div className="skills-content">
                    <div className="section-header">
                        <span data-aos='fade-down' data-aos-delay='200'> OUR SKILLS</span>          
                    </div>
                    {
                        SkillsData.map( (el,indx) => {

                            countAosSkill = countAosSkill + 200;

                            return(
                                <div ref={elementRefs[indx]} className='skill-single' data-goal={el.skillValue}  key={indx}>
                                    <div className='skill-header'>
                                        <h5 data-aos='fade-down' data-aos-delay={countAosSkill}>{el.skillTitle}</h5>
                                        <div className='skill-value-num'>{skillValueElements[indx]}%</div> 
                                    </div>
                                    <div className='skill-body'>
                                        <span className='skill-value'></span>
                                        <span style={{width:skillValueElements[indx] + '%'}} className='skill-value bg-skill-value'></span>
                                    </div>
                                </div>
                            )
                            
                        })
                        
                    }
                </div>
            </div>
        </section>
    )
}
export default Skills;
